import { IonButton} from "@ionic/react";
import {useState,useEffect} from "react";
import './adduser.css';
import UserList from './userList'

export default function FruitForm() {
  const fruited={
    Name:"",
    Quantity:0,
    edit:false
  };
  const [fruit, setfruit] = useState(fruited);
  const [tasks, setTasks] = useState([]);
  const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setfruit((prevState) => ({
      ...prevState,
      [e.target.id]: e.target.value,
    }));
    console.log(fruit)
  };

  const onSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setfruit(fruit);
    fetch('/api/tasks/add', {
      method: 'post',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ Name: fruit.Name,Quantity:fruit.Quantity, edit:"false" }),
    }).then(() => {
      getTasks();
      console.log(JSON.stringify({ Name: fruit.Name,Quantity:fruit.Quantity, edit:"false" }))
    });
  };
  const getTasks = () => {
    fetch('/api/tasks')
      .then(res => res.json())
      .then(setTasks);
  };

  useEffect(() => {
    getTasks();
  }, []);



  return (
    <div id='submitted'>
      <form onSubmit={onSubmit}>
        <label className='labels' htmlFor="Name">Name: </label>
        <input type="text" id="Name"  onChange={onChange} />
        <label className='labels' htmlFor="Quantity">Quantity: </label>
        <input  type="number" id="Quantity"  onChange={onChange} />
        <IonButton color="success" type="submit">Add Grocery Item</IonButton>
      </form>
<UserList tasks={tasks} updateTasks={getTasks}></UserList>
   </div>
    
  );
}
