import { IonInput,IonContent,IonButton,IonCard,IonCardHeader,IonCardTitle,IonCardSubtitle,IonCardContent, IonItem } from "@ionic/react";
import {useState,useEffect,useRef} from "react";
import './adduser.css'

const fruited={
  Name:"",
  Quantity:0,
  edit:false
};
const fruitup={
  Name1:"",
  Quantity1:0,
  edit1:false

}


export default function App() {
  const [fruit, setfruit] = useState(fruited);
  const { Name, Quantity } = fruit;
  const[edit,setEdit]=useState(false);
  const[updated,setUpdate]=useState(fruitup);
  const {Name1,Quantity1}=updated;

  const [updateList,setUpdateList]=useState([]);
  const [fruitList,setFruitList]=useState([]);
  const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setfruit((prevState) => ({
      ...prevState,
      [e.target.id]: e.target.value,
    }));
  };
  const firstRun = useRef(true);
  const handleRemoveItem = index => {
    console.log("test",index)
    setFruitList(fruitList.filter((item,i) => i !== index))
};
const handleEdit= (index) =>{
 if(fruitList[index].edit==false){
  const copied=[...fruitList]
  const item2 = {...fruitList[1]};
  item2.edit= true;
  item2.Name='test'
  copied[index]=item2;
  setFruitList(copied);
  console.log("correct",fruitList);
}else{
  const copied=[...fruitList]
  const item2 = {...fruitList[1]};
  item2.edit=true;
  copied[index]=item2
  setFruitList(copied)
alert("test")}
};
const onChangeEdit = (e: React.ChangeEvent<HTMLInputElement>) => {
  setUpdate((prevState) => ({
    ...prevState,
    [e.target.id]: e.target.value,
  }));
};
 
  const onSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setfruit(fruit);
    setFruitList([...fruitList,fruit]);
  };
  const onSubmitEdit =(index)=> (e: React.FormEvent<HTMLFormElement>) =>{
    e.preventDefault();
    setfruit(fruit);
    setFruitList([...fruitList,fruit]);
 
    // setFruitList(fruitList.filter((i) => i !== index))
    // setFruitList([...fruitList,updated]);

    // setUpdate(updated);
    // const copyed1=[...fruitList]
    // setFruitList(copyed1.splice(index,1,updated));
    // setEdit(false)
  };

  useEffect(() => {
    if (firstRun.current){
      firstRun.current=false;
      return
    }
     console.log(fruitList);
  }, [fruitList]); 

  return (
    <div id='submitted'>
      <form onSubmit={onSubmit}>
        <label className='labels' htmlFor="Name">Name</label>
        <input type="text" id="Name" value={Name} onChange={onChange} />
        <label className='labels' htmlFor="Quantity">Quantity</label>
        <input  type="number" id="Quantity" value={Quantity} onChange={onChange} />
        <IonButton color="success" type="submit">Add Grocery Item</IonButton>
      </form>

      {fruitList.map((item,index)=>{
        return(
          <div key={index}>
        <IonCard>
          <IonCardHeader >
            <IonCardTitle  >
              Item: {item.Name} 
            </IonCardTitle>
            <IonCardSubtitle >
              Quantity: {item.Quantity}
            </IonCardSubtitle>
          </IonCardHeader>
          <IonButton color="danger"onClick={() =>handleRemoveItem(index)}>Remove Item </IonButton>
          <IonButton key={index} color="warning" onClick={()=>handleEdit(index)}>Edit Item </IonButton>
       </IonCard>
       {fruitList[index].edit? <div key={index}>
         <form onSubmit={onSubmitEdit(index)}>
        <label className='labels' htmlFor="Name">Name</label>
        <input type="text" id="Name" value={Name} onChange={onChange} />
        <label className='labels' htmlFor="Quantity">Quantity</label>
        <input  type="number" id="Quantity" value={Quantity} onChange={onChange} />
        <IonButton color="success" type="submit">Add Grocery Item</IonButton>
      </form></div>:null }
        </div>)
      
    })
  } 

   </div>
    
  );
}
