import { IonItem } from "@ionic/react";
import React from "react";
import { useForm, SubmitHandler } from "react-hook-form";
import './adduser.css'
type FormValues = {
  Name: string,
  Price:number;
};

export default function FruitForm() {
  const { register, handleSubmit } = useForm<FormValues>();

  const [fruitlist,setFruitList]=React.useState<FormValues[]>([]);
  const onSubmit: SubmitHandler<FormValues> = data => 
  setFruitList([...fruitlist, data]);
  console.log(fruitlist)
  const handleRemoveItem = name => {
    setFruitList(fruitlist.filter(item => item.Name !== name))
}
 
;


  return (
    <div>
    <form onSubmit={handleSubmit(onSubmit)}>
      <input {...register("Name")} />

      <input type="submit" />
    </form>
    {fruitlist.map(fruitlist => <div>{fruitlist.Name}<span className='delete' onClick={() =>handleRemoveItem(fruitlist.Name)}>x </span>
      </div>)}

    </div>
  );
}
