import { IonItem } from "@ionic/react";
import React from "react";
import { useForm, SubmitHandler } from "react-hook-form";
import './adduser.css'
type FormValues = {
  Name: string,
  Price:number;
};

export default function FruitForm() {
  const { register, handleSubmit } = useForm<FormValues>();
  const [fruitlist,setFruitList]=React.useState<FormValues[]>([]);
  const[edit,setEdit]=React.useState(false);
  const onSubmit: SubmitHandler<FormValues> = data => 
  setFruitList([...fruitlist, data]);
  console.log(fruitlist)
  const handleRemoveItem = name => {
    setFruitList(fruitlist.filter(item => item.Name !== name))
};
const handleEdit = name=>{
  setFruitList(fruitlist.filter(item=>item.Name==name));
}

;


  return (
    <div>
    <form onSubmit={handleSubmit(onSubmit)}>
      Fruit Name:
      <input {...register("Name")} />
      Fruit Price: 
      <input {...register("Price")}/>
      <input className='submit' type="submit" value="Add Fruit"/>
    </form>
    {fruitlist.map(fruitlist => 
    <div><p className='list'>Name:</p> {fruitlist.Name} <p className='list'>Price:</p> ${fruitlist.Price}
      <span className='delete' onClick={() =>handleRemoveItem(fruitlist.Name)}>DELETE</span>   
      <span className='update' onClick={() => setEdit(true) }>Update</span>
      {edit ? 'currently' : 'not'}
    </div>)}

    </div>
  );
}
