import React, { useState } from "react";
import FruitTable from "./fruitTable";
import EditFruit from "./editFruit";
import AddFruit from "./addFruit";

const App = () => {
  const fruitsData = [
    { id: 1, name: "AAA", fruitname: "aiueo" },
    { id: 2, name: "BBB", fruitname: "kakikukeko" },
    { id: 3, name: "CCC", fruitname: "sasisuseso" }
  ];

  const [fruits, setfruits] = useState(fruitsData);

  const addfruit = fruit => {
    fruit.id = fruits.length + 1;
    setfruits([...fruits, fruit]);
  };

  const deletefruit = id => {
    setfruits(fruits.filter(fruit => fruit.id !== id));
  };

  const [editing, setEditing] = useState(false);
  const initialFormState = { id: null, name: "", fruitname: "" };
  const [currentfruit, setCurrentfruit] = useState(initialFormState);

  const editRow = fruit => {
    setEditing(true);
    setCurrentfruit({ id: fruit.id, name: fruit.name, fruitname: fruit.fruitname });
  };

  const updatefruit = (id, updatefruit) => {
    setEditing(false);
    setfruits(fruits.map(fruit => (fruit.id === id ? updatefruit : fruit)));
  };

  return (
    <div className="container">
      <h1>CRUD App with Hooks</h1>
      <div className="flex-row">
        <div className="flex-large">
          {editing ? (
            <div>
              <h2>Eidt fruit</h2>
              <EditFruit
                editing={editing}
                setEditing={setEditing}
                currentfruit={currentfruit}
                updatefruit={updatefruit}
              />
            </div>
          ) : (
            <div>
              <h2>Add fruit</h2>
              <AddFruit addfruit={addfruit} />
            </div>
          )}
        </div>
        <div>
          <h2>View fruits</h2>
          <FruitTable fruits={fruits} deletefruit={deletefruit} editRow={editRow} />
        </div>
      </div>
    </div>
  );
};

export default App;
