import React from "react";
import { useForm, SubmitHandler } from "react-hook-form";

type FormValues = {
  firstName: string;
};

export default function FruitForm() {
  const { register, handleSubmit } = useForm<FormValues>();

  const [fruitlist,setFruitList]=React.useState<FormValues[]>([]);
  const onSubmit: SubmitHandler<FormValues> = data => 
  setFruitList([...fruitlist, data]);
  console.log(fruitlist)
  const fruits = fruitlist;  // Essentially does: const vals = this.state.vals;

;


  return (
    <div>
    <form onSubmit={handleSubmit(onSubmit)}>
      <input {...register("firstName")} />

      <input type="submit" />
    </form>
    {fruits.map(fruits => <div>{fruits.firstName}</div>)}


    </div>
  );
}
