import React, { useState, useEffect } from 'react'

const EditfruitForm = (props: { currentfruit: any; updatefruit:any ; setEditing: boolean;}) => {
  const [ fruit, setfruit ] = useState(props.currentfruit)

  useEffect(
    () => {
      setfruit(props.currentfruit)
    },
    [ props ]
  )
  const handleInputChange = (event: { target: { name: any; value: any } }) => {
    const { name, value } = event.target

    setfruit({ ...fruit, [name]: value })
  }

  return (
    <form
      onSubmit={event => {
        event.preventDefault()

        props.updatefruit(fruit.id, fruit)
      }}
    >
      <label>Name</label>
      <input type="text" name="name" value={fruit.name} onChange={handleInputChange} />
      <label>Price</label>
      <input type="text" name="price" value={fruit.price} onChange={handleInputChange} />
      <button>Update fruit</button>
      <button onClick={() => props.setEditing(false)} >
        Cancel
      </button>
    </form>
  )
}

export default EditfruitForm;
