import React, { useState, useEffect } from "react";

const EditFruit = props => {
  const [fruit, setfruit] = useState(props.currentfruit);

  useEffect(
    () => {
      setfruit(props.currentfruit);
    },
    [props]
  );

  const handleInputChange = event => {
    const { name, value } = event.target;
    setfruit({ ...fruit, [name]: value });
  };

  return (
    <form
      onSubmit={event => {
        event.preventDefault();
        props.updatefruit(fruit.id, fruit);
      }}
    >
      <label>Name</label>
      <input
        type="text"
        name="name"
        value={fruit.name}
        onChange={handleInputChange}
      />
      <label>fruitname</label>
      <input
        type="text"
        name="fruitname"
        value={fruit.fruitname}
        onChange={handleInputChange}
      />
      <button>Update fruit</button>
      <button
        onClick={() => props.setEditing(false)}
        className="button muted-button"
      >
        Cancel
      </button>
    </form>
  );
};

export default EditFruit;
