import { IonItem,IonButton } from "@ionic/react";
import {useState,useEffect,useRef} from "react";
import './adduser.css'

const fruited={
  Name:"",
  id:null,
  Price:null
};



export default function App() {
  const [fruit, setfruit] = useState(fruited);
  const { Name, Price } = fruit;
  const [fruitList,setFruitList]=useState([]);
  const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setfruit((prevState) => ({
      ...prevState,
      [e.target.id]: e.target.value,
    }));
  };
  const firstRun = useRef(true);
  const handleRemoveItem = id => {
    setFruitList(fruitList.filter(item => item.id !== id))
}
 
  const onSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
     console.log(fruit);
    setfruit(fruit);
    fruit.id=fruitList.length+1
    setFruitList([...fruitList,fruit]);
  };
  useEffect(() => {
    if (firstRun.current){
      firstRun.current=false;
      return
    }
    //  console.log(fruitList);
  }, [fruitList]); 

  return (
    <div id='submitted'>

      <form onSubmit={onSubmit}>
        <label className='labels' htmlFor="Name">Name</label>
        <input className='forms'type="text" id="Name" value={Name} onChange={onChange} />
        <label className='labels' htmlFor="Price">Price</label>
        <input className='forms' type="number" id="Price" value={Price} onChange={onChange} />
        <IonButton color="success" type="submit">Add Fruit</IonButton>
        
      </form>
      {fruitList.map(item => {
    return ( 
        <>
        <IonButton color="danger" onClick={() =>handleRemoveItem(item.id)}>x </IonButton>
 {item.Name}
        </>);
})}
    </div>
  );
}
