import { IonContent,IonButton,IonCard,IonCardHeader,IonCardTitle,IonCardSubtitle,IonCardContent, IonItem } from "@ionic/react";
import {useState,useEffect,useRef} from "react";
import './adduser.css'

const fruited={
  Name:"",
  itemNum:0,
  Quantity:0
};



export default function App() {
  const [fruit, setfruit] = useState(fruited);
  const { Name, Quantity } = fruit;
  const [fruitList,setFruitList]=useState([]);
  const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setfruit((prevState) => ({
      ...prevState,
      [e.target.id]: e.target.value,
    }));
  };
  const firstRun = useRef(true);
  const handleRemoveItem = itemNum => {
    console.log(itemNum)
    setFruitList(fruitList.filter(item => item.itemNum !== itemNum))
}
 
  const onSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setfruit(fruit);
    fruit.itemNum++
    console.log(fruit.itemNum)
    setFruitList([...fruitList,fruit]);
  };
  useEffect(() => {
    if (firstRun.current){
      firstRun.current=false;
      return
    }
     console.log(fruitList);
  }, [fruitList]); 

  return (
    <div id='submitted'>
      <form onSubmit={onSubmit}>
        <label className='labels' htmlFor="Name">Name</label>
        <input className='forms'type="text" id="Name" value={Name} onChange={onChange} />
        <label className='labels' htmlFor="Quantity">Quantity</label>
        <input className='forms' type="number" id="Quantity" value={Quantity} onChange={onChange} />
        <IonButton color="success" type="submit">Add Fruit</IonButton>
        
      </form>
      {fruitList.map(item=>{
        return<>

<IonCard>
          <IonCardHeader >
            <IonCardTitle  >
              Item: {item.Name}
            </IonCardTitle>
            <IonCardSubtitle >
              Quantity: {item.Quantity}
            </IonCardSubtitle>
          </IonCardHeader>
          <IonButton color="danger"onClick={() =>handleRemoveItem(item.itemNum)}>Remove Item </IonButton>
</IonCard>

        </>
      })
    } 
   </div>
    
  );
}
