import React, { useState } from 'react'

const AddfruitForm = (props: { addfruit: (arg0: { id: null; name: string; price: string }) => void }) => {
	const firstMenu = { id: null, name: '', price: '' }
	const [ fruit, setfruit ] = useState(firstMenu)

	const handleInputChange = (event: { target: { name: any; value: any } }) => {
		const { name, value } = event.target

		setfruit({ ...fruit, [name]: value })
	}

	return (
		<form
			onSubmit={event => {
				event.preventDefault()
				if (!fruit.name || !fruit.price) return

				props.addfruit(fruit)
				setfruit(firstMenu)
			}}
		>
			<label>Name</label>
			<input type="text" name="name" value={fruit.name} onChange={handleInputChange} />
			<label>Price</label>
			<input type="text" name="price" value={fruit.price} onChange={handleInputChange} />
			<button>Add new item</button>
		</form>
	)
}

export default AddfruitForm
